mapper.update:
UPDATE /* com.greedy.waterfall.board.model.mapper.MeetingMapper#increseCount() */
       TBL_BOARD A
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                          FROM TBL_BOARD B
                         WHERE B.BOARD_NO = #{ meetingNo }
                         ) + 1
 WHERE A.BOARD_NO = #{ meetingNo }

mapper.insert:
INSERT /* com.greedy.waterfall.board.model.mapper.MeetingMapper#registMeetingFile() */
  INTO TBL_FILE A
(
  A.FILE_NO
, A.FILE_PATH
, A.FILE_ORIGIN_NAME
, A.FILE_RANDOM_NAME
, A.REF_BOARD_NO
, A.FILE_CATEGORY_NO
)
VALUES
( 
  FILE_NO.NEXTVAL
, #{ filePath }
, #{ fileOriginName }
, #{ fileRandomName }
, #{ refBoardNo }
, 3
)

mapper.select:
SELECT /* com.greedy.waterfall.board.model.mapper.MeetingMapper#findMeetingList() */ A.RNUM , A.BOARD_NO , A.BOARD_POSTING_DATE , A.BOARD_TITLE , A.BOARD_CONTENT , A.BOARD_STATUS , A.PROJECT_NO , A.BOARD_CATEGORY_NO , A.BOARD_UPDATED_DATE , A.MEMBER_NAME , A.BOARD_COUNT FROM (SELECT ROWNUM RNUM , B.BOARD_NO , B.BOARD_POSTING_DATE , B.BOARD_TITLE , B.BOARD_CONTENT , B.BOARD_STATUS , B.PROJECT_NO , B.BOARD_CATEGORY_NO , B.BOARD_UPDATED_DATE , B.MEMBER_NAME , B.BOARD_COUNT FROM (SELECT C.BOARD_NO , C.BOARD_POSTING_DATE , C.BOARD_TITLE , C.BOARD_CONTENT , C.BOARD_STATUS , C.PROJECT_NO , C.BOARD_CATEGORY_NO , C.BOARD_UPDATED_DATE , D.MEMBER_NAME , C.BOARD_COUNT FROM TBL_BOARD C JOIN TBL_MEMBER D ON (C.MEMBER_NO = D.MEMBER_NO)
C.BOARD_TITLE LIKE '%' || #{ searchValue } || '%'
C.BOARD_CONTENT LIKE '%'|| #{ searchValue } || '%'
D.MEMBER_NAME LIKE '%'|| #{ searchValue } || '%'
AND C.BOARD_CATEGORY_NO = 4 AND C.BOARD_STATUS = 'Y' AND C.PROJECT_NO = #{ projectNo }
ORDER BY C.BOARD_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
)A LEFT JOIN TBL_FILE E ON (A.BOARD_NO = E.REF_BOARD_NO) WHERE RNUM >= #{ startRow } ORDER BY A.BOARD_NO DESC

mapper.update:
UPDATE /* com.greedy.waterfall.board.model.mapper.MeetingMapper#modifyMeetingBoard() */
       TBL_BOARD A
   SET BOARD_UPDATED_DATE = SYSDATE
     , A.BOARD_TITLE = #{ title }
     , A.BOARD_CONTENT = #{ content }
 WHERE A.BOARD_NO = #{ no }
