mapper.update:
		UPDATE 
               TBL_BOARD A
           SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT
                                   FROM TBL_BOARD B
                                  WHERE B.BOARD_NO = #{ no }
                                ) + 1
         WHERE A.BOARD_NO = #{ no }

UPDATE 
       TBL_BOARD A
   SET A.BOARD_TITLE = #{ title }
     , A.BOARD_CONTENT = #{ content }
     , A.BOARD_UPDATED_DATE = SYSDATE
 WHERE A.BOARD_NO = #{ no }
