mapper.update:
UPDATE 
       TBL_BOARD A 
   SET A.BOARD_COUNT = (SELECT B.BOARD_COUNT 
                          FROM TBL_BOARD B 
                         WHERE BOARD_NO = #{ boardNo }
                       ) + 1
 WHERE A.BOARD_NO = #{ boardNo }

mapper.select:
SELECT RNUM , A.PROJECT_NO , A.PROJECT_NAME , A.PROJECT_START_DATE , A.PROJECT_DEADLINE , A.PROJECT_PROGRESSION , A.PROJECT_STATUS , A.PROJECT_COMPLETED_DATE , A.PROJECT_PROGRESS_STATUS_CODE , A.MEMBER_NAME FROM (SELECT ROWNUM RNUM , B.PROJECT_NO , B.PROJECT_NAME , B.PROJECT_START_DATE , B.PROJECT_DEADLINE , B.PROJECT_PROGRESSION , B.PROJECT_STATUS , B.PROJECT_COMPLETED_DATE , B.PROJECT_PROGRESS_STATUS_CODE , B.MEMBER_NAME FROM (SELECT C.PROJECT_NO , C.PROJECT_NAME , C.PROJECT_START_DATE , C.PROJECT_DEADLINE , C.PROJECT_PROGRESSION , C.PROJECT_STATUS , C.PROJECT_COMPLETED_DATE , C.PROJECT_PROGRESS_STATUS_CODE , E.MEMBER_NAME FROM TBL_PROJECT C JOIN TBL_ROLE_ASSIGNMENT_HISTORY D ON (C.PROJECT_NO = D.PROJECT_NO) JOIN TBL_MEMBER E ON(D.MEMBER_NO = E.MEMBER_NO)
C.PROJECT_NAME LIKE '%' || #{ searchValue } || '%'
E.MEMBER_NAME LIKE '%' || #{ searchValue } || '%'
AND D.ROLE_NO = 8 AND C.PROJECT_STATUS = 'Y'
ORDER BY C.PROJECT_NO ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE RNUM >= #{ startRow }

SELECT RNUM , A.PROJECT_NO , A.PROJECT_NAME , A.PROJECT_START_DATE , A.PROJECT_DEADLINE , A.PROJECT_PROGRESSION , A.PROJECT_STATUS , A.PROJECT_COMPLETED_DATE , A.PROJECT_PROGRESS_STATUS_CODE , A.MEMBER_NAME FROM (SELECT ROWNUM RNUM , B.PROJECT_NO , B.PROJECT_NAME , B.PROJECT_START_DATE , B.PROJECT_DEADLINE , B.PROJECT_PROGRESSION , B.PROJECT_STATUS , B.PROJECT_COMPLETED_DATE , B.PROJECT_PROGRESS_STATUS_CODE , B.MEMBER_NAME FROM (SELECT C.PROJECT_NO , C.PROJECT_NAME , C.PROJECT_START_DATE , C.PROJECT_DEADLINE , C.PROJECT_PROGRESSION , C.PROJECT_STATUS , C.PROJECT_COMPLETED_DATE , C.PROJECT_PROGRESS_STATUS_CODE , E.MEMBER_NAME FROM TBL_PROJECT C JOIN TBL_ROLE_ASSIGNMENT_HISTORY D ON (C.PROJECT_NO = D.PROJECT_NO) JOIN TBL_MEMBER E ON(D.MEMBER_NO = E.MEMBER_NO)
C.PROJECT_NAME LIKE '%' || #{ searchValue } || '%'
E.MEMBER_NAME LIKE '%' || #{ searchValue } || '%'
AND D.ROLE_NO = 8 AND C.PROJECT_STATUS = 'N'
ORDER BY C.PROJECT_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE RNUM >= #{ startRow }

SELECT A.RNUM , A.PROJECT_NO , A.PROJECT_NAME , A.PROJECT_START_DATE , A.PROJECT_DEADLINE , A.PROJECT_PROGRESSION , A.PROJECT_STATUS , A.PROJECT_COMPLETED_DATE , A.PROJECT_PROGRESS_STATUS_CODE , A.MEMBER_NAME FROM (SELECT ROWNUM RNUM , B.PROJECT_NO , B.PROJECT_NAME , B.PROJECT_START_DATE , B.PROJECT_DEADLINE , B.PROJECT_PROGRESSION , B.PROJECT_STATUS , B.PROJECT_COMPLETED_DATE , B.PROJECT_PROGRESS_STATUS_CODE , B.MEMBER_NAME FROM (SELECT C.PROJECT_NO , C.PROJECT_NAME , C.PROJECT_START_DATE , C.PROJECT_DEADLINE , C.PROJECT_PROGRESSION , C.PROJECT_STATUS , C.PROJECT_COMPLETED_DATE , C.PROJECT_PROGRESS_STATUS_CODE , E.MEMBER_NAME FROM TBL_PROJECT C JOIN TBL_ROLE_ASSIGNMENT_HISTORY D ON (C.PROJECT_NO = D.PROJECT_NO) JOIN TBL_MEMBER E ON(D.MEMBER_NO = E.MEMBER_NO)
C.PROJECT_NAME LIKE '%' || #{ searchValue } || '%'
AND D.MEMBER_NO = #{ projectNo } AND D.ROLE_HISTORY_NO IN (SELECT MAX(F.ROLE_HISTORY_NO) FROM TBL_ROLE_ASSIGNMENT_HISTORY F WHERE F.ROLE_NO = 8 GROUP BY F.PROJECT_NO ) AND D.ROLE_NO = 8 AND C.PROJECT_STATUS = 'Y'
ORDER BY C.PROJECT_NO DESC ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow }

SELECT RNUM , A.PROJECT_NO , A.PROJECT_NAME , A.PROJECT_START_DATE , A.PROJECT_DEADLINE , A.PROJECT_PROGRESSION , A.PROJECT_STATUS , A.PROJECT_COMPLETED_DATE , A.PROJECT_PROGRESS_STATUS_CODE , A.MEMBER_NAME FROM (SELECT ROWNUM RNUM , B.PROJECT_NO , B.PROJECT_NAME , B.PROJECT_START_DATE , B.PROJECT_DEADLINE , B.PROJECT_PROGRESSION , B.PROJECT_STATUS , B.PROJECT_COMPLETED_DATE , B.PROJECT_PROGRESS_STATUS_CODE , B.MEMBER_NAME FROM (SELECT D.PROJECT_NO , D.PROJECT_NAME , D.PROJECT_START_DATE , D.PROJECT_DEADLINE , D.PROJECT_PROGRESSION , D.PROJECT_STATUS , D.PROJECT_COMPLETED_DATE , D.PROJECT_PROGRESS_STATUS_CODE , E.MEMBER_NAME FROM TBL_PROJECT_MEM_HISTORY C JOIN TBL_PROJECT D ON (C.PROJECT_NO = D.PROJECT_NO) JOIN TBL_MEMBER E ON (C.MEMBER_NO = E.MEMBER_NO)
E.MEMBER_NAME LIKE '%' || #{ searchValue } || '%'
D.PROJECT_NAME LIKE '%' || #{ searchValue } || '%'
AND D.PROJECT_NO IN (SELECT F.PROJECT_NO FROM TBL_ROLE_ASSIGNMENT_HISTORY F WHERE F.MEMBER_NO = #{ projectNo } AND F.ROLE_NO != 8 GROUP BY F.PROJECT_NO ) AND D.PROJECT_STATUS = 'Y' AND C.MEMBER_NO = #{ projectNo }
ORDER BY D.PROJECT_NO ) B
WHERE ROWNUM <= #{ endRow }
) A WHERE RNUM >= #{ startRow }

mapper.update:
UPDATE
       TBL_PROJECT A
   SET A.PROJECT_NAME = #{ projectName }
     , A.PROJECT_START_DATE = #{ startDate }
     , A.PROJECT_DEADLINE = #{ deadLine }
     , A.PROJECT_PROGRESSION = #{ progression }
     , A.PROJECT_PROGRESS_STATUS_CODE = #{ projectStatusCode }
 WHERE A.PROJECT_NO = #{ projectNo }

mapper.select:
SELECT 
       A.MEMBER_NO
  FROM TBL_PROJECT_MEM_HISTORY A
 WHERE A.MEMBER_NO = #{ pmNumber }
   AND A.PROJECT_NO = #{ projectNo }
   AND A.PROJECT_MEM_DROP_DATE = NULL

mapper.delete:
DELETE
  FROM TBL_PROJECT_MEM_HISTORY A
 WHERE A.PROJECT_NO = #{ projectNo }
   AND A.MEMBER_NO = #{ pmNumber }

DELETE
  FROM TBL_PROJECT A
 WHERE A.PROJECT_NO = #{ projectNo }

DELETE
  FROM TBL_ROLE_ASSIGNMENT_HISTORY A
 WHERE A.PROJECT_NO = #{ projectNo }
   AND A.MEMBER_NO = #{ pmNumber }
